import React, { useCallback, useState } from "react";
import { Form, Button } from "react-bootstrap";
import { useNavigation } from "react-navi";
import {
  LocalStorage,
  USERNAME_FIELD_NAME,
} from "../../localstorage/LocalStorage";

interface FormData {
  meetingID?: string;
  username?: string;
}

interface FormErrors {
  meetingID: boolean;
  username: boolean;
}

const JoinMeeting = () => {
  const [formData, setFormData] = useState<FormData>({});
  const [formErrors, setFormErrors] = useState<FormErrors>({
    meetingID: true,
    username: true,
  });

  const validateFormData = useCallback(
    (formData: FormData) => {
      console.log(formData);
      if (!formData.meetingID || formData.meetingID.length === 0) {
        setFormErrors({
          meetingID: true,
          username: formErrors.username,
        });
      } else {
        setFormErrors({
          meetingID: false,
          username: formErrors.username,
        });
      }
      if (!formData.username || formData.username.length === 0) {
        setFormErrors({
          username: true,
          meetingID: formErrors.username,
        });
      } else {
        setFormErrors({
          username: false,
          meetingID: formErrors.username,
        });
      }
    },
    [formErrors]
  );

  const navigation = useNavigation();

  const handleJoinMeetingClicked = useCallback(() => {
    if (!formErrors.username && !formErrors.meetingID && formData.username) {
      LocalStorage.setToLocalStorage(USERNAME_FIELD_NAME, formData.username);
      navigation.navigate(`/meeting/${formData.meetingID}`);
    }
  }, [navigation, formData, formErrors]);

  const handleMeetingIDChanged = useCallback(
    (newMeetingId: string) => {
      setFormData({
        meetingID: newMeetingId,
        username: formData.username,
      });
      validateFormData({
        meetingID: newMeetingId,
        username: formData.username,
      });
    },
    [formData, validateFormData]
  );

  const handleUsernameChanged = useCallback(
    (newUsername: string) => {
      setFormData({
        meetingID: formData.meetingID,
        username: newUsername,
      });
      validateFormData({
        meetingID: formData.meetingID,
        username: newUsername,
      });
    },
    [formData, validateFormData]
  );

  return (
    <div
      style={{
        alignContent: "center",
        margin: "auto",
        marginTop: "50px",
        height: "50%",
        width: "40%",
        backgroundColor: "burlywood",
        paddingTop: "100px",
        paddingBottom: "100px",
        paddingLeft: "50px",
        paddingRight: "50px",
      }}
    >
      <Form className="text-center">
        <Form.Group className="mb-3" controlId="meetingId">
          <Form.Label>Meeting Id</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter meeting Id"
            onChange={(event) => {
              handleMeetingIDChanged(event.target.value);
            }}
            isInvalid={formErrors.meetingID}
          />
          <Form.Control.Feedback type="invalid">
            Please enter valid meeting id.
          </Form.Control.Feedback>
        </Form.Group>

        <Form.Group className="mb-3" controlId="username">
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter username"
            onChange={(event) => {
              handleUsernameChanged(event.target.value);
            }}
            isInvalid={formErrors.username}
          />
          <Form.Control.Feedback type="invalid">
            Please enter username.
          </Form.Control.Feedback>
        </Form.Group>
        <Button
          variant="primary"
          type="submit"
          onClick={(event) => {
            event.preventDefault();
            handleJoinMeetingClicked();
          }}
        >
          Join meeting
        </Button>
      </Form>
    </div>
  );
};

export default JoinMeeting;
