import { Endpoint } from ".";

const baseUrl = "http://localhost:8080";

export interface PostResponse {
    error?: string,
    data?: any,
}

const post = ((endpointUrl: Endpoint, body: string) => {
window.fetch(baseUrl + endpointUrl, {
    method: 'POST',
    body: body,
  }).then(result => {
      return {
        data: result,
      } as PostResponse;
  }).catch(err => {
      console.log(err);
      return err;
  });
});

export default post;
