import { createChunksMatcher } from '../createChunksMatcher';
import { createChunk } from '../Chunks';
export function withStatus(maybeResolvableStatus, child) {
    return createChunksMatcher(maybeResolvableStatus, child, function (status, request) {
        return status ? [createChunk('status', request, { status: status })] : [];
    });
}
