import Endpoint from ".";

const baseUrl = "http://localhost:8080";

const post = (async (endpointUrl: Endpoint, body: string) => {
    return await window.fetch(baseUrl + endpointUrl, {
    method: 'POST',
    headers: {
      'content-type': 'application/json;charset=UTF-8',
    },
    body: body,
  })
});

export default post;
