import React, { useCallback, useState } from "react";
import { Form, Button } from "react-bootstrap";
import { useNavigation } from "react-navi";
import { Endpoint, RegisterUserInput } from "../../../http";
import post, { PostResponse } from "../../../http/Http";
import {
  USERNAME_FIELD_NAME,
  USER_ID_FIELD_NAME,
} from "../../../localstorage/LocalStorage";

interface FormData {
  email?: string;
  username?: string;
}

interface FormErrors {
  email: boolean;
  username: boolean;
  requestError: boolean;
}

const UpdateProfile = () => {
  const [formData, setFormData] = useState<FormData>({});
  const [formErrors, setFormErrors] = useState<FormErrors>({
    email: true,
    username: true,
    requestError: false,
  });

  const isEmailValid = useCallback((email?: string): boolean => {
    const regex =
      /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return regex.test(String(email).toLowerCase());
  }, []);

  const validateData = useCallback(
    (dataToValidate: FormData) => {
      const errors: FormErrors = {
        email: false,
        requestError: false,
        username: false,
      };
      if (!isEmailValid(dataToValidate.email)) {
        errors.email = true;
      }
      if (
        dataToValidate.username === undefined ||
        dataToValidate.username.length === 0
      ) {
        errors.username = true;
      }
      setFormErrors(errors);
    },
    [isEmailValid]
  );

  const handleEmailChanged = useCallback(
    (newEmail?: string) => {
      setFormData({
        ...formData,
        email: newEmail,
      });
      validateData({
        ...formData,
        email: newEmail,
      });
    },
    [formData, validateData]
  );

  const handleUsernameChanged = useCallback(
    (newusername?: string) => {
      setFormData({
        ...formData,
        username: newusername,
      });
      validateData({
        ...formData,
        username: newusername,
      });
    },
    [formData, validateData]
  );

  const handleUpdateProfileClicked = useCallback(async () => {
    const response = await post(
      Endpoint.RegisterUserEndpoint,
      JSON.stringify({
        email: formData.email,
        username: formData.username,
      } as RegisterUserInput)
    );
    if ((response as unknown as PostResponse).error) {
      setFormErrors({
        ...formErrors,
        requestError: true,
      });
      return;
    }
    localStorage.setItem(USERNAME_FIELD_NAME, formData.username ?? "");
    localStorage.setItem(USER_ID_FIELD_NAME, response.data);
  }, [formData, formErrors]);

  return (
    <div
      style={{
        alignContent: "center",
        margin: "auto",
        marginTop: "50px",
        height: "50%",
        width: "40%",
        backgroundColor: "burlywood",
        paddingTop: "100px",
        paddingBottom: "100px",
        paddingLeft: "50px",
        paddingRight: "50px",
      }}
    >
      <Form className="text-center">
        <Form.Group className="mb-3">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter email"
            onChange={(event) => {
              handleEmailChanged(event.target.value);
            }}
            isInvalid={formErrors.email}
          />
          <Form.Control.Feedback type="invalid">
            Please enter valid email.
          </Form.Control.Feedback>
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter username"
            onChange={(event) => {
              handleUsernameChanged(event.target.value);
            }}
            isInvalid={formErrors.username}
          />
          <Form.Control.Feedback type="invalid">
            Please enter username.
          </Form.Control.Feedback>
        </Form.Group>

        <Button
          variant="primary"
          type="submit"
          onClick={(event) => {
            event.preventDefault();
            handleUpdateProfileClicked();
          }}
        >
          Update
        </Button>

        {formErrors.requestError ? (
          <Form.Control.Feedback type="invalid">
            Something went wrong.
          </Form.Control.Feedback>
        ) : null}
      </Form>
    </div>
  );
};

export default UpdateProfile;
